glsl3.vert:
precision highp float;
uniform CCGlobal {
  vec4 cc_time;

  vec4 cc_screenSize;

  vec4 cc_screenScale;

  vec4 cc_nativeSize;

  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;

  vec4 cc_exposure;

  vec4 cc_mainLitDir;

  vec4 cc_mainLitColor;

  vec4 cc_ambientSky;
  vec4 cc_ambientGround;
};
uniform CCLocal {
  mat4 cc_matWorld;
  mat4 cc_matWorldIT;
};

in vec3 a_position;
in vec4 a_color;
out vec4 v_color;

#if USE_TEXTURE
in vec2 a_uv0;
out vec2 v_uv0;
#endif

void main () {
  vec4 pos = vec4(a_position, 1);

  #if CC_USE_MODEL
  pos = cc_matViewProj * cc_matWorld * pos;
  #else
  pos = cc_matViewProj * pos;
  #endif

  #if USE_TEXTURE
  v_uv0 = a_uv0;
  #endif

  v_color = a_color;

  gl_Position = pos;
}

glsl3.frag:
precision highp float;

#if USE_ALPHA_TEST

  uniform ALPHA_TEST {
    float alphaThreshold;
  }
#endif

void ALPHA_TEST (in vec4 color) {
  #if USE_ALPHA_TEST
      if (color.a < alphaThreshold) discard;
  #endif
}

void ALPHA_TEST (in float alpha) {
  #if USE_ALPHA_TEST
      if (alpha < alphaThreshold) discard;
  #endif
}

in vec4 v_color;

#if USE_TEXTURE
in vec2 v_uv0;
uniform sampler2D texture;

uniform RANGE {
  float range;
}
#endif

void main () {
  vec4 o = vec4(0.0);

  float x = floor(v_uv0.x * range);
  float y = floor(v_uv0.y * range);

  o += texture(texture, vec2(x * (1.0 / range), y * (1.0 / range)));

  o *= v_color;

  ALPHA_TEST(o);

  gl_FragColor = o;
}

glsl1.vert:
precision highp float;
uniform mat4 cc_matViewProj;
uniform mat4 cc_matWorld;

attribute vec3 a_position;
attribute vec4 a_color;
varying vec4 v_color;

#if USE_TEXTURE
attribute vec2 a_uv0;
varying vec2 v_uv0;
#endif

void main () {
  vec4 pos = vec4(a_position, 1);

  #if CC_USE_MODEL
  pos = cc_matViewProj * cc_matWorld * pos;
  #else
  pos = cc_matViewProj * pos;
  #endif

  #if USE_TEXTURE
  v_uv0 = a_uv0;
  #endif

  v_color = a_color;

  gl_Position = pos;
}

glsl1.frag:
precision highp float;

#if USE_ALPHA_TEST

  uniform float alphaThreshold;
#endif

void ALPHA_TEST (in vec4 color) {
  #if USE_ALPHA_TEST
      if (color.a < alphaThreshold) discard;
  #endif
}

void ALPHA_TEST (in float alpha) {
  #if USE_ALPHA_TEST
      if (alpha < alphaThreshold) discard;
  #endif
}

varying vec4 v_color;

#if USE_TEXTURE
varying vec2 v_uv0;
uniform sampler2D texture;

uniform float range;
#endif

void main () {
  vec4 o = vec4(0.0);

  float x = floor(v_uv0.x * range);
  float y = floor(v_uv0.y * range);

  o += texture2D(texture, vec2(x * (1.0 / range), y * (1.0 / range)));

  o *= v_color;

  ALPHA_TEST(o);

  gl_FragColor = o;
}
